import React from 'react';
import useForm from 'react-hook-form';

export default function ContactForm() {
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => console.log(data);
  console.log(errors);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input type="text" placeholder="First name" name="First name" ref={register({required: true, maxLength: 80})} /> <br/>
      <input type="text" placeholder="Last name" name="Last name" ref={register({required: true, maxLength: 100})} /> <br/>
      <input type="tel" placeholder="Phone number" name="Phone number" ref={register({required: true, minLength: 6, maxLength: 12})} /> <br/>
      <input type="text" placeholder="Email" name="Email" ref={register({required: true, pattern: /^\S+@\S+$/i})} /> <br/>
      <input type="submit" />
    </form>
  );
}
