import React from 'react';
import useForm from 'react-hook-form';
import {
    BrowserRouter as Router,
    Switch,
    Route,
    NavLink,
} from "react-router-dom";
import Home from "../home/Home";

export default function LoginForm() {

  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => localStorage.setItem(document.getElementById("Username").value, document.getElementById("Password").value);
  console.log(errors);

  return (
    <Router>
    <form onSubmit={handleSubmit(onSubmit)} >
      <input id="Username" type="text" placeholder="Username" name="Username" ref={register({required: true, min: 3, maxLength: 10})} /><br/>
      <input id="Password" type="text" placeholder="Password" name="Password" ref={register({required: true, maxLength: 20})} /> <br/>

      <NavLink to="/home/" className="nav-link">
          <input type="submit" />
      </NavLink>

    </form>

    <Switch>
        <Route path="/home" component={Home} />
    </Switch>
    </Router>
  );
}
