import React from 'react';
import useForm from 'react-hook-form';

export default function App() {
  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => localStorage.setItem("user", value);
  console.log(errors);

  return (
    <form onSubmit={handleSubmit(onSubmit)}>
      <input type="text" placeholder="Username" name="Username" ref={register({required: true, min: 3, maxLength: 10})} />
      <input type="text" placeholder="Password" name="Password" ref={register({required: true, maxLength: 20})} />

      <input type="submit" />
    </form>
  );
}
