import React from 'react';
import useForm from 'react-hook-form';

export default function LoginForm() {

  const { register, handleSubmit, errors } = useForm();
  const onSubmit = data => localStorage.setItem("user", document.getElementById("Username").value, document.getElementById("Password").value  );
  console.log(errors);

  return (
    <form onSubmit={handleSubmit(onSubmit)} action="index.html">
      <input id="Username" type="text" placeholder="Username" name="Username" ref={register({required: true, min: 3, maxLength: 10})} /><br/>
      <input id="Password" type="text" placeholder="Password" name="Password" ref={register({required: true, maxLength: 20})} /> <br/>

      <input type="submit" />
    </form>
  );
}
